package com.financia.business.member.service.impl;import java.util.List;import com.financia.business.MemberGrade;import com.financia.business.member.mapper.MemberGradeMapper;import com.financia.business.member.service.IMemberGradeService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * 会员等级Service业务层处理 *  * @author 花生 * @date 2022-08-05 */@Servicepublic class MemberGradeServiceImpl implements IMemberGradeService{    @Autowired    private MemberGradeMapper memberGradeMapper;    /**     * 查询会员等级     *      * @param id 会员等级主键     * @return 会员等级     */    @Override    public MemberGrade selectMemberGradeById(Long id)    {        return memberGradeMapper.selectMemberGradeById(id);    }    /**     * 查询会员等级列表     *      * @param memberGrade 会员等级     * @return 会员等级     */    @Override    public List<MemberGrade> selectMemberGradeList(MemberGrade memberGrade)    {        return memberGradeMapper.selectMemberGradeList(memberGrade);    }    /**     * 新增会员等级     *      * @param memberGrade 会员等级     * @return 结果     */    @Override    public int insertMemberGrade(MemberGrade memberGrade)    {        return memberGradeMapper.insertMemberGrade(memberGrade);    }    /**     * 修改会员等级     *      * @param memberGrade 会员等级     * @return 结果     */    @Override    public int updateMemberGrade(MemberGrade memberGrade)    {        return memberGradeMapper.updateMemberGrade(memberGrade);    }    /**     * 批量删除会员等级     *      * @param ids 需要删除的会员等级主键     * @return 结果     */    @Override    public int deleteMemberGradeByIds(Long[] ids)    {        return memberGradeMapper.deleteMemberGradeByIds(ids);    }    /**     * 删除会员等级信息     *      * @param id 会员等级主键     * @return 结果     */    @Override    public int deleteMemberGradeById(Long id)    {        return memberGradeMapper.deleteMemberGradeById(id);    }}